  `signup_blood_height` int(11) NOT NULL,
  `signup_blood_landau` int(11) NOT NULL,
  `signup_blood_macbenh` int(11) NOT NULL,
  `signup_blood_sutcan` int(11) NOT NULL,
  `signup_blood_noihach` int(11) NOT NULL,
  `signup_blood_phauthuat` int(11) NOT NULL,
  `signup_blood_xamminh` int(11) NOT NULL,
  `signup_blood_duoctruyenmau` int(11) NOT NULL,
  `signup_blood_matuy` int(11) NOT NULL,
